import React from 'react';
import Validate from './ValidateInfo';
import './Form.css';

const SignUp = () => {
  return (
    <div>
      <h1>
        Get started with us today! Create your account by filling out the
        information below.
      </h1>
      First Name: <input type='text' placeholder='Enter your first name' />
      Last Name: <input type='text' placeholder='Enter your last name' />
      Email: <input type='text' placeholder='Enter your email' />
      Phone no: <input type='text' placeholder='Enter your phone number' />
      Address: <input type='text' placeholder='Enter your full Address' />
      <button type='submit'>Sign Up</button>
      <span>
        Already have an account? Sign In <a href='/signin'>here</a>
      </span>
    </div>
  );
};

export default SignUp;

